import React, { useEffect } from 'react'
import './ClassicInput.scss'

function ClassicInput({children, type, placeholder, textArea}) {
    useEffect(() => {
        textArea=false;
  }, []);
  return (

    <div className="classic-input-container">
        <label>{children}</label>
        {textArea===true && <input placeholder={placeholder} type={type}/>}
        {textArea===false && <textArea placeholder={placeholder}/>}
    </div>
  )
}

export default ClassicInput
